function getComments(n,t){inCallBack||(inCallBack=!0,$.ajax({url:t,method:"post",dataType:"html",data:{itemId:itemIden,Time:n.toJSON()},success:function(n){dateTimeSend=new Date;n!==""&&$("#comment-wrapper").append(n);inCallBack=!1},error:function(n){console.log(n)}}))}function OnCommentSuccess(){getComments(dateTimeSend,"/Comment/CommentPage/")}let dateTimeSend=new Date(0),itemIden=$("#ItemIdentificator").val();const timeInterval=5e3;let inCallBack=!1;OnCommentSuccess();setInterval(OnCommentSuccess,timeInterval);$("#ItemId").val(itemIden);